mapper.select:
SELECT

FROM message
WHERE 1 = 1

  AND jmxtime >= #{starttime,jdbcType=TIMESTAMP}


  AND jmxtime < #{endtime,jdbcType=TIMESTAMP}


  AND hostip = #{hostip,jdbcType=VARCHAR}


  AND port = #{port,jdbcType=VARCHAR}

ORDER BY id DESC
LIMIT #{currIndex} , #{pageSize}

SELECT
  COUNT(*) NUM
FROM message
WHERE 1 = 1

  AND jmxtime >= #{starttime,jdbcType=TIMESTAMP}


  AND jmxtime < #{endtime,jdbcType=TIMESTAMP}


  AND hostip = #{hostip,jdbcType=VARCHAR}


  AND port = #{port,jdbcType=VARCHAR}

SELECT
CASE WHEN locate(#{jqStr,jdbcType=VARCHAR}, content) != 0
THEN
SUBSTRING_INDEX(content,#{jqStr,jdbcType=VARCHAR},-1)
ELSE '0' END val,
jmxtime
FROM message
WHERE 1 = 1

  AND jmxtime >= #{starttime,jdbcType=TIMESTAMP}


  AND jmxtime < #{endtime,jdbcType=TIMESTAMP}


  AND hostip = #{hostip,jdbcType=VARCHAR}


  AND port = #{port,jdbcType=VARCHAR}


  AND title LIKE #{alarmKey,jdbcType=VARCHAR}

ORDER BY jmxtime asc

SELECT
'1' val,
jmxtime
FROM message
WHERE 1 = 1

  AND jmxtime >= #{starttime,jdbcType=TIMESTAMP}


  AND jmxtime < #{endtime,jdbcType=TIMESTAMP}


  AND hostip = #{hostip,jdbcType=VARCHAR}


  AND port = #{port,jdbcType=VARCHAR}


  AND title LIKE #{alarmKey,jdbcType=VARCHAR}

ORDER BY jmxtime asc

mapper.insert:

insert into message (id, hostip, servername, 
  port, title, rule, 
  content, watchtime, jmxtime
  )
values (#{id,jdbcType=BIGINT}, #{hostip,jdbcType=VARCHAR}, #{servername,jdbcType=VARCHAR}, 
  #{port,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, #{rule,jdbcType=VARCHAR}, 
  #{content,jdbcType=VARCHAR}, #{watchtime,jdbcType=VARCHAR}, #{jmxtime,jdbcType=TIMESTAMP}
  )
